// Inicializar transcripción echoAI
const echo = new EchoAI.Transcription({
  apiKey: process.env.ECHO_API_KEY,
  options: {
    language: 'es-ES',
    latency: 'ultra-low',
  }
});

// Comenzar a escuchar el stream de audio
echo.listen(audioStream);

// Eventos de transcripción en tiempo real
