// Initialiser la transcription echoAI
const echo = new EchoAI.Transcription({
  apiKey: process.env.ECHO_API_KEY,
  options: {
    language: 'fr-FR',
    latency: 'ultra-low',
  }
});

// Commencer à écouter le flux audio
echo.listen(audioStream);

// Événements de transcription en temps réel
echo.on('transcription', (result) => {
  console.log('Transcription en direct:', result.text);
});
