// echoAI 文字起こしを初期化
const echo = new EchoAI.Transcription({
  apiKey: process.env.ECHO_API_KEY,
  options: {
    language: 'ja-JP',
    latency: 'ultra-low',
  }
});

// 音声ストリームをリッスン開始
echo.listen(audioStream);

// リアルタイム文字起こしイベント
echo.on('transcription', (result) => {
  console.log('ライブ文字起こし:', result.text);
});
